import React, { useState } from 'react';
import axios from 'axios';

const ItemForm = ({ itemType }) => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        price: 0,
        // Add more fields as needed based on your backend model
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, formData);
            console.log('Item created:', response.data);
            // Optionally, reset form fields or perform other actions after successful submission
            setFormData({
                name: '',
                description: '',
                price: 0,
            });
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required />

            <label>Description:</label>
            <textarea name="description" value={formData.description} onChange={handleChange} required />

            <label>Price:</label>
            <input type="number" name="price" value={formData.price} onChange={handleChange} required />

            {/* Add more form fields as needed based on your backend model */}

            <button type="submit">Submit</button>
        </form>
    );
};

export default ItemForm;
