import React, { useState } from 'react';

const TrackOrder = ({ progress }) => {
    const steps = [33, 66, 100]; // Define the progress at which each step is accomplished

    return (
        <div className="p-4">
            <h1 className="text-xl font-bold mb-4">Track Your Order</h1>
            <div className="w-full bg-gray-200 rounded-full flex justify-between mb-4">
                {steps.map((step, index) => (
                    <div
                        key={index}
                        className={`w-8 h-8 rounded-full flex items-center justify-center ${
                            progress >= step ? 'bg-green-500' : 'bg-yellow-300'
                        }`}
                    >
                        <span className="text-white font-bold">{index + 1}</span>
                    </div>
                ))}
            </div>
            <div className="w-full bg-gray-200 rounded-full h-6">
                <div
                    className="bg-green-500 h-6 rounded-full transition-all duration-300"
                    style={{ width: `${progress}%` }}
                />
            </div>
        </div>
    );
};

const App = () => {
    const [progress, setProgress] = useState(0); // Initialize progress state to 0

    // Function to simulate order progress
    const simulateProgress = () => {
        setProgress((prevProgress) => {
            if (prevProgress >= 100) {
                return 0; // Reset progress to 0 if it's 100 or more
            } else {
                return prevProgress + 10; // Otherwise, increase progress by 10
            }
        });
    };

    return (
        <div className="flex flex-col items-center p-6">
            <TrackOrder progress={progress} />
            <button
                className="mt-4 bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700 transition duration-300"
                onClick={simulateProgress}
            >
                Simulate Progress
            </button>
        </div>
    );
};

export default App;
