import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import CustomerService from '../services/CustomerServices';

const AddCustomerComponent = ({ onSwitchToLogin }) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone_no, setPhoneNo] = useState('');
    const [location, setLocation] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const saveCustomer = (e) => {
        e.preventDefault();

        const customer = { name, email, phone_no, location, password };
        
        CustomerService.createCustomer(customer).then(() => {
            navigate('/customers');
        }).catch(error => {
            console.log(error);
        });
    };

    return (
        <>
            <h2 className="text-center text-2xl font-bold mb-6">Sign Up</h2>
            <form onSubmit={saveCustomer}>
                <div className="mb-4">
                    <label className="block text-gray-700">Name</label>
                    <input
                        type="text"
                        className="w-full px-4 py-2 border rounded-lg mt-1"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700">Email Address</label>
                    <input
                        type="email"
                        className="w-full px-4 py-2 border rounded-lg mt-1"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700">Phone No</label>
                    <input
                        type="text"
                        className="w-full px-4 py-2 border rounded-lg mt-1"
                        value={phone_no}
                        onChange={(e) => setPhoneNo(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700">Location</label>
                    <input
                        type="text"
                        className="w-full px-4 py-2 border rounded-lg mt-1"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700">Password</label>
                    <input
                        type="password"
                        className="w-full px-4 py-2 border rounded-lg mt-1"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-success w-100 mb-3">Sign Up</button>
                <button type="button" className="btn btn-link w-100" onClick={onSwitchToLogin}>
                    Already have an account? Login
                </button>
            </form>
        </>
    );
};

export default AddCustomerComponent;
