import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [reward, setReward] = useState('');
    const [status, setStatus] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8090/api/v1/users');
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users', error);
            toast.error('Error fetching users');
        }
    };

    const handleRewardUpload = async (userId) => {
        try {
            await axios.post(`http://localhost:8090/api/v1/users/${userId}/upload-reward`, { reward });
            toast.success('Reward uploaded successfully');
            fetchUsers(); // Refresh user list
        } catch (error) {
            console.error('Error uploading reward', error);
            toast.error('Error uploading reward');
        }
    };

    const handleStatusUpdate = async (userId) => {
        try {
            await axios.put(`http://localhost:8090/api/v1/users/${userId}/update-status`, { status });
            toast.success('Status updated successfully');
            fetchUsers(); // Refresh user list
        } catch (error) {
            console.error('Error updating status', error);
            toast.error('Error updating status');
        }
    };

    return (
        <div>
            <h1>Admin Dashboard</h1>
            <table>
                <thead>
                    <tr>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Phone No</th>
                        <th>Location</th>
                        <th>Image</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                            <td>{user.phone_no}</td>
                            <td>{user.location}</td>
                            <td><img src={user.image} alt="User" width="50" height="50" /></td>
                            <td>
                                <input
                                    type="text"
                                    placeholder="Reward"
                                    value={reward}
                                    onChange={(e) => setReward(e.target.value)}
                                />
                                <button onClick={() => handleRewardUpload(user.id)}>Upload Reward</button>
                                <input
                                    type="text"
                                    placeholder="Status"
                                    value={status}
                                    onChange={(e) => setStatus(e.target.value)}
                                />
                                <button onClick={() => handleStatusUpdate(user.id)}>Update Status</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
