// src/components/Profile.js
import React from 'react';

const Profile = ({ profilePicture, name, email, bio }) => {
    return (
        <div className="flex flex-col items-center bg-gray-900 text-white shadow-lg rounded-lg p-6 max-w-sm mx-auto">
            <div className="relative">
                <img 
                    className="w-32 h-32 rounded-full object-cover border-4 border-white shadow-md"
                    src={profilePicture}
                    alt={`${name}'s profile`}
                />
                <div className="absolute bottom-0 right-0 bg-green-500 border-2 border-white rounded-full w-6 h-6"></div>
            </div>
            <h2 className="mt-4 text-xl font-semibold">{name}</h2>
            <p className="mt-2">{email}</p>
            <p className="mt-2">{bio}</p>
            <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition duration-300 ease-in-out">
                Edit Profile
            </button>
        </div>
    );
};

export default Profile;
