import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import DropdownMenu from './DropdownMenu';
import GoogleMap from './GoogleMap';
import Logo from '../assets/Frame 1 (1).png'

const Dashboard = ({ user }) => {
    const { name, rewards, pickupScheduled, pickupDate, profilePicture, recentActivities, previousOrders } = user;

    const [menuOpen, setMenuOpen] = useState(false);
    const [userLocation, setUserLocation] = useState(null);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const handleMenuItemClick = (menuItem) => {

      

        if (menuItem === 'Dispose') {
            window.open('/dispose', '_blank');
        } else {
            // Handle other menu items if needed
        }
    };

    useEffect(() => {
        // Fetch user's location using Geolocation API
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                setUserLocation({ lat: latitude, lng: longitude });
            },
            (error) => {
                console.error('Error getting user location:', error);
            }
        );
    }, []); // Run only once on component mount

    

    const getInitials = (name) => {
        const nameArray = name.split(' ');
        const initials = nameArray.map(part => part[0].toUpperCase()).join('');
        return initials;
    };

    const renderRecentActivities = () => {
        if (previousOrders && previousOrders.length > 0) {
            return previousOrders.map((order, index) => (
                <li key={index}>Ordered: {order.itemName} - {order.date}</li>
            ));
        } else {
            return <li>No previous orders</li>;
        }
    };

    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                <div className="logo"><img src = {Logo} alt ="Logo"></img></div>
                <div className="menu-icon" onClick={toggleMenu}>
                    &#9776;
                </div>
                <DropdownMenu isOpen={menuOpen} onMenuItemClick={handleMenuItemClick} />
            </header>
            <main className="dashboard-main">
                <div className="info-container">
                    <section className="profile-box">
                        {profilePicture ? (
                            <img src={profilePicture} alt={`${name}'s profile`} className="profile-picture" />
                        ) : (
                            <div className="profile-initials">{getInitials(name)}</div>
                        )}
                        <h1>Hello, {name}!</h1>
                    </section>
                    <section className="rewards-box">
                        <p>Rewards Earned: <span className="rewards">{rewards}</span></p>
                    </section>
                </div>
                <section className="pickup-section">
                    <h2>Pickup Scheduled</h2>
                    <div className="notification">
                        {pickupScheduled ? (
                            <p><i className="icon">📅</i> You have a pickup scheduled on {pickupDate}.</p>
                        ) : (
                            <p><i className="icon">📅</i> No pickup scheduled.</p>
                        )}
                        <div className="map-section">
                            {userLocation && <GoogleMap center={userLocation} />}
                        </div>
                    </div>
                </section>
                <section className="recent-activities">
                    <h2>Recent Activities</h2>
                    <ul>
                        {recentActivities && recentActivities.length > 0 ? (
                            recentActivities.map((activity, index) => (
                                <li key={index}>{activity}</li>
                            ))
                        ) : (
                            renderRecentActivities()
                        )}
                    </ul>
                </section>
            </main>
        </div>
    );
};

export default Dashboard;
