import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';


const DisposeForm = () => {
    const { itemType } = useParams();
    const navigate = useNavigate();
    const [brand, setBrand] = useState('');
    const [yearsUsed, setYearsUsed] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Prepare form data to be sent to the backend
        const formData = new FormData();
        formData.append('brand', brand);
        formData.append('yearsUsed', yearsUsed);
        formData.append('description', description);
        formData.append('image', image);

        try {
            // Send form data to the backend
            const response = await fetch(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, {
                method: 'POST',
                body: formData,
                headers: {
                    'Accept': 'application/json', // Ensure the response is handled correctly
                },
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            console.log('Success:', data);
            navigate('/dispose'); // Redirect after successful submission
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className="flex justify-center items-center h-screen bg-gradient-to-r from-gray-200 to-white">
            <h1 className="text-2xl text-gray-900 font-bold mb-9 p-4">Dispose {itemType}</h1>
            <div className="w-1/2 p-4 bg-white rounded shadow">
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label className="block text-gray-900 text-sm font-bold mb-2">Brand:</label>
                        <input
                            type="text"
                            placeholder="Enter brand"
                            name="brand"
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            value={brand}
                            onChange={(e) => setBrand(e.target.value)}
                        />
                    </div>

                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2">Number of Years Used:</label>
                        <input
                            type="number"
                            placeholder="Enter number of years used"
                            name="yearsUsed"
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            value={yearsUsed}
                            onChange={(e) => setYearsUsed(e.target.value)}
                        />
                    </div>

                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2">Description:</label>
                        <textarea
                            placeholder="Enter description"
                            name="description"
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </div>

                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2">Upload Image:</label>
                        <input
                            type="file"
                            name="image"
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            onChange={(e) => setImage(e.target.files[0])}
                        />
                    </div>

                    <div className="flex items-center justify-between">
                        <button 
                            type="submit" 
                            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        >
                            Submit
                        </button>
                        <button 
                            type="button" 
                            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                            onClick={() => navigate('/dispose')}
                        >
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default DisposeForm;
