// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import DropdownMenu from './DropdownMenu';
import GoogleMap from './GoogleMap';
import Logo from '../assets/Frame 1 (1).png';

const Dashboard = ({ user }) => {
    const { name, rewards, pickupScheduled, pickupDate, profilePicture, recentActivities, previousOrders } = user;

    const [menuOpen, setMenuOpen] = useState(false);
    const [userLocation, setUserLocation] = useState(null);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const handleMenuItemClick = (menuItem) => {
        setMenuOpen(false); // Close menu after clicking
    };

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                setUserLocation({ lat: latitude, lng: longitude });
            },
            (error) => {
                console.error('Error getting user location:', error);
            }
        );
    }, []);

    const getInitials = (name) => {
        const nameArray = name.split(' ');
        const initials = nameArray.map(part => part[0].toUpperCase()).join('');
        return initials;
    };

    const renderRecentActivities = () => {
        if (previousOrders && previousOrders.length > 0) {
            return previousOrders.map((order, index) => (
                <li key={index} className="mb-2 text-white">{`Ordered: ${order.itemName} - ${order.date}`}</li>
            ));
        } else {
            return <li className="mb-2 text-white">No previous orders</li>;
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-green-500 to-teal-500">
            <header className="flex justify-between items-center p-5 bg-gradient-to-r from-blue-600 to-indigo-600 text-white shadow-lg">
                <div className="logo">
                    <img src={Logo} alt="Logo" className="w-24 h-auto" />
                </div>
                <div className="text-2xl cursor-pointer" onClick={toggleMenu}>
                    &#9776;
                </div>
                <DropdownMenu isOpen={menuOpen} onMenuItemClick={handleMenuItemClick} />
            </header>
            <main className="flex-1 p-10">
                <div className="flex flex-col md:flex-row justify-between mb-10 space-y-6 md:space-y-0 md:space-x-6">
                    <section className="bg-gradient-to-r from-gray-800 to-gray-600 p-8 rounded-lg shadow-lg transition-transform transform hover:scale-105 flex flex-col items-center">
                        {profilePicture ? (
                            <img src={profilePicture} alt={`${name}'s profile`} className="w-30 h-30 rounded-full mb-5 shadow-md" />
                        ) : (
                            <div className="w-30 h-30 rounded-full bg-gradient-to-r from-blue-600 to-blue-900 text-white flex items-center justify-center text-4xl font-bold mb-5">
                                {getInitials(name)}
                            </div>
                        )}
                        <h1 className="text-2xl text-white">{`Hello, ${name}!`}</h1>
                    </section>
                    <section className="bg-gradient-to-r from-gray-800 to-gray-600 p-8 rounded-lg shadow-lg transition-transform transform hover:scale-105 flex flex-col items-center">
                        <p className="text-lg text-white">Rewards Earned: <span className="font-bold text-yellow-400">{rewards}</span></p>
                    </section>
                </div>
                <section className="bg-gradient-to-r from-blue-500 to-blue-700 p-8 rounded-lg shadow-lg mb-6 transition-transform transform hover:scale-105">
                    <h2 className="text-xl text-white mb-4">Pickup Scheduled</h2>
                    <div className="notification">
                        {pickupScheduled ? (
                            <p className="text-white flex items-center"><i className="icon mr-2">ðŸ“…</i> You have a pickup scheduled on {pickupDate}.</p>
                        ) : (
                            <p className="text-white flex items-center"><i className="icon mr-2">ðŸ“…</i> No pickup scheduled.</p>
                        )}
                        <div className="mt-4 w-full h-96 relative rounded-lg overflow-hidden">
                            {userLocation && <GoogleMap center={userLocation} />}
                        </div>
                    </div>
                </section>
                <section className="bg-gradient-to-r from-blue-500 to-blue-700 p-8 rounded-lg shadow-lg mb-6 transition-transform transform hover:scale-105">
                    <h2 className="text-xl text-white mb-4">Recent Activities</h2>
                    <ul className="list-none p-0 m-0">
                        {recentActivities && recentActivities.length > 0 ? (
                            recentActivities.map((activity, index) => (
                                <li key={index} className="text-white mb-2 hover:text-green-300 transition-colors">{activity}</li>
                            ))
                        ) : (
                            renderRecentActivities()
                        )}
                    </ul>
                </section>
            </main>
        </div>
    );
};

export default Dashboard;
