import React, { useState } from 'react';
import axios from 'axios';

const ItemForm = ({ itemType }) => {
    const [brand, setBrand] = useState('');
    const [yearsUsed, setYearsUsed] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('brand', brand);
        formData.append('yearsUsed', yearsUsed);
        formData.append('description', description);
        formData.append('image', image);

        try {
            const response = await axios.post(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, formData);

            if (!response.data) {
                throw new Error('Network response was not ok');
            }

            console.log('Item created:', response.data);
            // Optionally, perform additional actions after successful form submission
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Brand:</label>
                <input type="text" value={brand} onChange={(e) => setBrand(e.target.value)} />
            </div>
            <div>
                <label>Years Used:</label>
                <input type="number" value={yearsUsed} onChange={(e) => setYearsUsed(e.target.value)} />
            </div>
            <div>
                <label>Description:</label>
                <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
            </div>
            <div>
                <label>Image:</label>
                <input type="file" onChange={(e) => setImage(e.target.files[0])} />
            </div>
            <button type="submit">Submit</button>
        </form>
    );
};

export default ItemForm;
