// export default Dashboard;

import React, { useState, useEffect, useRef } from 'react';
import './Dashboard.css';
import DropdownMenu from './DropdownMenu.js';
import GoogleMap from './GoogleMap';
import Logo from '../assets/Frame 1 (1).png';

const Dashboard = ({ user }) => {
    const { name, rewards, pickupScheduled, pickupDate, profilePicture, recentActivities, previousOrders, feedbacks  } = user;

    const [menuOpen, setMenuOpen] = useState(false);
    const [userLocation, setUserLocation] = useState(null);
    const menuRef = useRef(null);

      const toggleMenu = () => {
       setMenuOpen(!menuOpen);
     };

     const handleMenuItemClick = (menuItem) => {
        if (menuItem === 'Dispose') {
            window.open('/dispose', '_blank');
        } else {
            // Handle other menu items if needed
        }
    };

     const handleClickOutside = (event) => {
         if (menuRef.current && !menuRef.current.contains(event.target)) {
            setMenuOpen(false);
         }
     };


    useEffect(() => {
              navigator.geolocation.getCurrentPosition(
                    (position) => {
                         const { latitude, longitude } = position.coords;
                         setUserLocation({ lat: latitude, lng: longitude });
                     },
                     (error) => {
                         console.error('Error getting user location:', error);
                     }
                );
    
                document.addEventListener('mousedown', handleClickOutside);
                        return () => {
                            document.removeEventListener('mousedown', handleClickOutside);
                         };
                     }, []);

    


    const getInitials = (name) => {
                const nameArray = name.split(' ');
                 const initials = nameArray.map(part => part[0].toUpperCase()).join('');
                return initials;
             };
        
             const renderRecentActivities = () => {
                if (previousOrders && previousOrders.length > 0) {
                 return previousOrders.map((order, index) => (
                         <li key={index} className="mb-2 text-white">{`Ordered: ${order.itemName} - ${order.date}`}</li>
                     ));
                 } else {
                     return <li className="mb-2 text-white">No previous orders</li>;
                 }
    
                };


    return (
        <div className="min-h-screen flex flex-col">
            <header className="flex justify-between items-center p-5 bg-gradient-to-r from-blue-600 to-indigo-600 text-white shadow-lg">
                <div className="menu-icon" onClick={toggleMenu} >
                    &#9776;
                </div>
                <div className="logo">
                <img src={Logo} alt="Logo" className="w-24 h-auto" />
                </div>
                <DropdownMenu isOpen={menuOpen} onMenuItemClick={handleMenuItemClick} />
            </header>

                      <main className="flex-1 flex flex-col md:flex-row">
                 <div className="flex-1 bg-black p-10 text-white">
                     <div className="flex flex-col md:flex-row justify-between mb-10 space-y-6 md:space-y-0 md:space-x-6">
                        <section className="bg-gray-800 p-8 rounded-lg shadow-lg transition-transform transform hover:scale-105 flex flex-col items-center">
                             {profilePicture ? (
                                <img src={profilePicture} alt={`${name}'s profile`} className="w-30 h-30 rounded-full mb-5 shadow-md" />
                            ) : (
                                <div className="w-30 h-30 rounded-full bg-blue-600 text-white flex items-center justify-center text-4xl font-bold mb-5">
                                    {getInitials(name)}
                                </div>
                            )}
                            <h1 className="text-2xl">{`Hello, ${name}!`}</h1>
                        </section>
                        <section className="bg-gray-800 p-8 rounded-lg shadow-lg transition-transform transform hover:scale-105 flex flex-col items-center">
                            <p className="text-lg">Rewards Earned: <span className="font-bold text-yellow-400">{rewards}</span></p>
                        </section>
                    </div>
                    <section className="bg-blue-600 p-8 rounded-lg shadow-lg mb-6 transition-transform transform hover:scale-105">
                        <h2 className="text-xl mb-4">Pickup Scheduled</h2>
                        <div className="notification">
                            {pickupScheduled ? (
                                <p className="flex items-center"><i className="icon mr-2">ðŸ“…</i> You have a pickup scheduled on {pickupDate}.</p>
                            ) : (
                                <p className="flex items-center"><i className="icon mr-2">ðŸ“…</i> No pickup scheduled.</p>
                            )}
                            <div className="mt-4 w-full h-96 relative rounded-lg overflow-hidden">
                                {userLocation && <GoogleMap center={userLocation} />}
                            </div>
                        </div>
                    </section>
                    <section className="bg-blue-600 p-8 rounded-lg shadow-lg mb-6 transition-transform transform hover:scale-105">
                        <h2 className="text-xl mb-4">Recent Activities</h2>
                        <ul className="list-none p-0 m-0">
                            {recentActivities && recentActivities.length > 0 ? (
                                recentActivities.map((activity, index) => (
                                    <li key={index} className="mb-2 hover:text-green-300 transition-colors">{activity}</li>
                                ))
                            ) : (
                                renderRecentActivities()
                            )}
                        </ul>
                    </section>
                </div>
                <div className="flex-1 bg-white p-10">
                    <section className="bg-gray-100 p-8 rounded-lg shadow-lg mb-6">
                        <h2 className="text-xl mb-4">User Feedback</h2>
                        <div className="feedback-content overflow-x-auto whitespace-nowrap flex space-x-6 py-4">
                            {feedbacks && feedbacks.length > 0 ? (
                                feedbacks.map((feedback, index) => (
                                    <div key={index} className="feedback-item bg-white shadow-lg rounded-lg p-4 flex-none w-64">
                                        {feedback.profilePicture ? (
                                            <img src={feedback.profilePicture} alt={`${feedback.name}'s profile`} className="w-12 h-12 rounded-full mb-2" />
                                        ) : (
                                            <div className="w-12 h-12 rounded-full bg-gray-300 text-gray-700 flex items-center justify-center text-xl font-bold mb-2">
                                                {getInitials(feedback.name)}
                                            </div>
                                        )}
                                        <p className="text-gray-800">{feedback.text}</p>
                                        <div className="text-yellow-500">
                                            {'â˜…'.repeat(feedback.rating)}{'â˜†'.repeat(5 - feedback.rating)}
                                        </div>
                                    </div>
                                ))
                            ) : (
                                <p className="text-gray-600">No feedback available.</p>
                            )}
                        </div>
                    </section>
                    <section className="bg-gray-100 p-8 rounded-lg shadow-lg">
                        <h2 className="text-xl mb-4">Submit Feedback</h2>
                        <form>
                            <div className="mb-4">
                                <label htmlFor="rating" className="block text-gray-700 mb-2">Rating</label>
                                <div className="flex space-x-1 text-yellow-500">
                                    <span className="cursor-pointer">â˜…</span>
                                    <span className="cursor-pointer">â˜…</span>
                                    <span className="cursor-pointer">â˜…</span>
                                    <span className="cursor-pointer">â˜…</span>
                                    <span className="cursor-pointer">â˜†</span>
                                </div>
                            </div>
                            <div className="mb-4">
                                <label htmlFor="feedback" className="block text-gray-700 mb-2">Feedback</label>
                                <textarea id="feedback" className="w-full p-2 border border-gray-300 rounded" rows="4"></textarea>
                            </div>
                            <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition">Submit</button>
                        </form>
                    </section>
                </div>
            </main>
        </div>
    );
};

export default Dashboard;
