import React, { useState } from 'react';

const TrackOrder = ({ progress }) => {
    const steps = [33, 66, 100]; // Define the progress at which each step is accomplished

    return React.createElement(
        'div',
        null,
        React.createElement('h1', null, 'Track Your Order'),
        React.createElement('div', { style: { width: '100%', backgroundColor: '#f3f3f3', borderRadius: '25px', display: 'flex', justifyContent: 'space-between' } },
            steps.map((step, index) => (
                React.createElement('div', { key: index, style: { width: '30px', height: '30px', backgroundColor: progress >= step ? '#4CAF50' : '#fefb72' } })
            ))
        ),
        React.createElement('div', { style: { width: progress + '%', height: '25px', backgroundColor: '#4CAF50', borderRadius: '25px' } })
    );
};

const App = () => {
    const [progress, setProgress] = useState(0); // Initialize progress state to 0

    // Function to simulate order progress
    const simulateProgress = () => {
        setProgress((prevProgress) => {
            if (prevProgress >= 100) {
                return 0; // Reset progress to 0 if it's 100 or more
            } else {
                return prevProgress + 10; // Otherwise, increase progress by 10
            }
        });
    };

    return React.createElement(
        'div',
        null,
        React.createElement(TrackOrder, { progress: progress }),
        React.createElement('button', { onClick: simulateProgress }, 'Simulate Progress')
    );
};

export default App;
