import React from 'react';
import './Login.css';
import Logo from '../assets/Frame 1 (1).png';

const Login = () => {
    const handleUserLogin = () => {
        window.location.href = '/user-login';
    };

    const handleAdminLogin = () => {
        window.location.href = '/admin-login';
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-indigo-800 text-white relative overflow-hidden">
            {/* Background animation */}
            <div className="absolute w-full h-full flex items-center justify-center">
                <div className="absolute bg-gray-200 opacity-10 w-48 h-48 rounded-full animate-float" style={{ animationDelay: '0s' }}></div>
                <div className="absolute bg-gray-200 opacity-10 w-32 h-32 rounded-full animate-float" style={{ animationDelay: '1s' }}></div>
                <div className="absolute bg-gray-200 opacity-10 w-24 h-24 rounded-full animate-float" style={{ animationDelay: '2s' }}></div>
            </div>
            
            <div className="w-full md:w-1/2 p-5 flex flex-col items-center bg-dark rounded-lg shadow-lg relative z-10">
                <img src={Logo} alt="Logo" className="w-24 h-auto mb-10" />
                <h1 className="text-2xl mb-4 text-center">Welcome! Please choose your login type</h1>
                <div className="w-full flex flex-col items-center space-y-4">
                    <button
                        className="w-full p-5 bg-blue-500 rounded-lg text-xl shadow-lg transition-transform transform hover:scale-105 cursor-pointer"
                        onClick={handleUserLogin}
                    >
                        User Login
                    </button>
                    <button
                        className="w-full p-5 bg-indigo-500 rounded-lg text-xl shadow-lg transition-transform transform hover:scale-105 cursor-pointer"
                        onClick={handleAdminLogin}
                    >
                        Admin Login
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Login;
