// src/components/DropdownMenu.js
import React from 'react';
import PropTypes from 'prop-types';

const DropdownMenu = ({ isOpen, onMenuItemClick, user }) => {
    if (!isOpen) {
        return null;
    }

    return (
        <div className="absolute top-0 left-0 w-64 bg-white shadow-lg p-4" onClick={onMenuItemClick}>
            <div className="flex items-center mb-4">
                {user.profilePicture ? (
                    <img src={user.profilePicture} alt={`${user.name}'s profile`} className="w-12 h-12 rounded-full mr-2" />
                ) : (
                    <div className="w-12 h-12 rounded-full bg-gray-300 text-gray-700 flex items-center justify-center text-xl font-bold mr-2">
                        {user.name.split(' ').map(part => part[0].toUpperCase()).join('')}
                    </div>
                )}
                <div>
                    <p className="text-gray-800 font-bold">{user.name}</p>
                </div>
            </div>
            <ul className="list-none p-0">
                <li className="mb-2"><a href="#" className="text-black hover:text-blue-600">Home</a></li>
                <li className="mb-2"><a href="#" className="text-black hover:text-blue-600">Profile</a></li>
                <li className="mb-2"><a href="#" className="text-black hover:text-blue-600">Settings</a></li>
                <li className="mb-2"><a href="#" className="text-black hover:text-blue-600">Logout</a></li>
            </ul>
        </div>
    );
};

DropdownMenu.propTypes = {
    isOpen: PropTypes.bool.isRequired,
    onMenuItemClick: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
};

export default DropdownMenu;
