import React from 'react';
import './DropdownMenu.css';
import { Link } from 'react-router-dom';

const DropdownMenu = ({ isOpen, onMenuItemClick, user }) => {
  return (
    <div className={`dropdown-menu ${isOpen ? 'show' : ''}`}>
      <div className="profile-section">
        {user.profilePicture ? (
          <img src={user.profilePicture} alt={`${user.name}'s profile`} className="profile-picture" />
        ) : (
          <div className="profile-initials">
            {user.name.split(' ').map(part => part[0].toUpperCase()).join('')}
          </div>
        )}
        <div className="profile-name">{user.name}</div>
      </div>
      <ul>
        <li onClick={() => onMenuItemClick('Profile')}><Link to="/profile">Profile</Link></li>
        <li onClick={() => onMenuItemClick('Dispose')}><Link to="/dispose">Dispose</Link></li>
        <li onClick={() => onMenuItemClick('Rewards/Points')}><Link to="/rewards">Rewards/Points</Link></li>
        <li onClick={() => onMenuItemClick('Track Order')}><Link to="/track-order">Track Order</Link></li>
        <li onClick={() => onMenuItemClick('Customer Support')}><Link to="/customer-support">Customer Support</Link></li>
        <li onClick={() => onMenuItemClick('About Us/Support Us')}><Link to="/about-us">About Us/Support Us</Link></li>
      </ul>
    </div>
  );
};

export default DropdownMenu;
