import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './DisposeForm.css';

const DisposeForm = () => {
    const { itemType } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        brand: '',
        yearsUsed: '',
        description: '',
        image: null,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        setFormData({ ...formData, image: e.target.files[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formDataToSend = new FormData();
        formDataToSend.append('brand', formData.brand);
        formDataToSend.append('yearsUsed', formData.yearsUsed);
        formDataToSend.append('description', formData.description);
        formDataToSend.append('image', formData.image);

        fetch(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, {
            method: 'POST',
            body: formDataToSend,
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                console.log('Success:', data);
                navigate('/dispose');
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    const handleCancel = () => {
        navigate('/dispose');
    };

    return (
        <div className="dispose-form-container">
            <h3>{itemType}</h3>
            <form className="dispose-form" onSubmit={handleSubmit}>
                <label>
                    Brand:
                    <input type="text" name="brand" value={formData.brand} onChange={handleChange} required />
                </label>
                <label>
                    Number of Years Used:
                    <input type="number" name="yearsUsed" value={formData.yearsUsed} onChange={handleChange} required />
                </label>
                <label>
                    Description:
                    <textarea name="description" value={formData.description} onChange={handleChange} required />
                </label>
                <label>
                    Upload Image:
                    <input type="file" name="image" onChange={handleFileChange} required />
                </label>
                <button type="submit">Submit</button>
                <button type="button" onClick={handleCancel}>Cancel</button>
            </form>
        </div>
    );
};

export default DisposeForm;
