// Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaFacebook, FaGoogle, FaTwitter } from 'react-icons/fa';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Dashboard from './Dashboard';

const Signup = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [user, setUser] = useState(null); // New state for storing user data
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8090/api/v1/customers/login', { email, password });
            console.log('Login successful', response.data);
            toast.success('Login successful!');
            setUser(response.data); // Store user data
        } catch (error) {
            console.error('Login failed', error.response.data);
            toast.error('Invalid email or password');
        }
    };

    if (user) {
        // If user exists, render the Dashboard component
        return <Dashboard user={user} />;
    }

    return (
        <div className="background-container">
            <div className="login-container">
                <h2 className="text-center mb-4">Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group mb-3">
                        <label>Email</label>
                        <input
                            type="email"
                            className="form-control"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group mb-3">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-success w-100 mb-3">Login</button>
                    <button
                        type="button"
                        className="btn btn-link w-100 mb-3"
                        onClick={() => navigate('/forgot-password')}
                    >
                        Forgot Password?
                    </button>
                </form>
                <div className="text-center mb-3">Or login with</div>
                <div className="d-flex justify-content-around mb-3">
                    <FaFacebook size={32} style={{ cursor: 'pointer' }} />
                    <FaGoogle size={32} style={{ cursor: 'pointer' }} />
                    <FaTwitter size={32} style={{ cursor: 'pointer' }} />
                </div>
                <button className="btn btn-primary w-100" onClick={() => navigate('/add-customer')}>Create New Account</button>
            </div>
            <ToastContainer />
        </div>
    );
};

export default Signup;
