import React, { useState } from 'react';
import './App.css';
import FooterComponent from './components/FooterComponent';
import HeaderComponent from './components/HeaderComponents'; // Corrected the import name
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Signup from './components/Signup';
import AddCustomerComponent from './components/AddCustomerComponent';
import ForgotPassword from './components/ForgotPassword';
import Dashboard from './components/Dashboard';
import DisposeMenu from './components/DisposeMenu';
import DisposeForm from './components/DisposeForm';
import TrackOrder from './components/TrackOrder';
import Profile from './components/Profile';
import ProtectedRoute from './components/ProtectedRoute'; // Import ProtectedRoute

function App() {
  const [user, setUser] = useState(null); // User is initially null

  const handleLoginSuccess = (userData) => {
    setUser(userData);
  };

  return (
    <div className="bg-gray-900 text-white">
      <Router>
        <HeaderComponent user={user} /> {/* Pass user to HeaderComponent if needed */}
        <div className="container">
          <Routes>
            <Route exact path="/" element={<Signup onLoginSuccess={handleLoginSuccess} />} />
            <Route path="/customers" element={<Signup onLoginSuccess={handleLoginSuccess} />} />
            <Route path="/add-customer" element={<AddCustomerComponent />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/dashboard" element={
              <ProtectedRoute user={user}>
                <Dashboard user={user} />
              </ProtectedRoute>
            } />
            <Route path="/dispose" element={
              <ProtectedRoute user={user}>
                <DisposeMenu />
              </ProtectedRoute>
            } />
            <Route path="/dispose/:itemType" element={
              <ProtectedRoute user={user}>
                <DisposeForm />
              </ProtectedRoute>
            } />
            <Route path="/track-order" element={
              <ProtectedRoute user={user}>
                <TrackOrder />
              </ProtectedRoute>
            } />
            <Route path="/profile" element={
              <ProtectedRoute user={user}>
                <Profile profilePicture={user?.profilePicture} name={user?.name} />
              </ProtectedRoute>
            } />
          </Routes>
        </div>
        <FooterComponent />
      </Router>
    </div>
  );
}

export default App;
