import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import './RewardsPage.css';

const RewardsPage = () => {
  const location = useLocation();
  const { computerId, points } = location.state || {};
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8090/api/v1/items/computers/${computerId}`);
        setUserDetails(response.data);
      } catch (err) {
        console.error('Error fetching user details:', err);
        setError('Failed to fetch user details');
      }
    };

    const allotPointsAndDeleteUser = async () => {
      try {
        await axios.post(`http://localhost:8090/api/v1/items/computers/${computerId}/rewards`, { points });
        await axios.delete(`http://localhost:8090/api/v1/items/computers/${computerId}`);
      } catch (err) {
        console.error('Error processing rewards or deleting user:', err);
        setError('Failed to process rewards or delete user');
      }
    };

    if (computerId && points) {
      fetchUserDetails();
      allotPointsAndDeleteUser();
    } else {
      setError('Invalid data');
    }

    setLoading(false);
  }, [computerId, points]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="rewards-page">
      <h2>Rewards Page</h2>
      <p>Points allotted: {points}</p>
      {userDetails && (
        <>
          <p>User Name: {userDetails.signupName}</p>
          <p>Email: {userDetails.signupEmail}</p>
          <p>Phone No: {userDetails.signupPhoneNo}</p>
          <p>Location: {userDetails.signupLocation}</p>
        </>
      )}
      <p>User details have been deleted.</p>
      <button onClick={() => window.location.href = '/'}>Go Back</button>
    </div>
  );
};

export default RewardsPage;
