import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const ItemForm = ({ itemType }) => {
    const [formData, setFormData] = useState({
        brand: '',
        yearsUsed: '',
        description: '',
        imageUrl: null  // For file upload
    });

    const history = useHistory();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setFormData({ ...formData, imageUrl: file });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('brand', formData.brand);
        formDataToSend.append('yearsUsed', formData.yearsUsed);
        formDataToSend.append('description', formData.description);
        if (formData.imageUrl) {
            formDataToSend.append('imageUrl', formData.imageUrl);
        }

        try {
            const response = await axios.post(
                `http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`,
                formDataToSend,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data' // Ensure correct Content-Type
                    }
                }
            );
            console.log('Item created:', response.data);
            history.push('/dispose'); // Redirect to DisposeMenu after successful submission
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    return (
        <div>
            <h2>Create {itemType}</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Brand:</label>
                    <input
                        type="text"
                        name="brand"
                        value={formData.brand}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div>
                    <label>Years Used:</label>
                    <input
                        type="number"
                        name="yearsUsed"
                        value={formData.yearsUsed}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea
                        name="description"
                        value={formData.description}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div>
                    <label>Image:</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileChange}
                    />
                </div>
                <button type="submit">Create {itemType}</button>
            </form>
        </div>
    );
};

export default ItemForm;
