import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const ItemForm = () => {
    const { itemType } = useParams();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        brand: '',
        yearsUsed: '', // Changed from 'years_used' to 'yearsUsed'
        description: '',
        image: null  // Changed from 'image_url' to 'image', corrected the key name
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleImageChange = (e) => {
        setFormData({ ...formData, image: e.target.files[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('brand', formData.brand);
        formDataToSend.append('yearsUsed', formData.yearsUsed);
        formDataToSend.append('description', formData.description);
        formDataToSend.append('image', formData.image);

        try {
            const response = await axios.post(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, formDataToSend);
            console.log('Item created:', response.data);
            navigate('/dispose'); // Redirect to DisposeMenu after successful submission
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    return (
        <div className="form-container">
            <h2>Add {itemType}</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="brand">Brand:</label>
                    <input
                        type="text"
                        id="brand"
                        name="brand"
                        value={formData.brand}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="yearsUsed">Years Used:</label>
                    <input
                        type="number"
                        id="yearsUsed"
                        name="yearsUsed"
                        value={formData.yearsUsed}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        rows={4} // Adjusted for a better mobile layout
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="image">Upload Image:</label>
                    <input
                        type="file"
                        id="image"
                        name="image"
                        accept="image/*" // Restrict file type to images
                        onChange={handleImageChange}
                        required
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default ItemForm;
