import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import HeaderComponent from './components/HeaderComponents';
import Authentication from './components/Authentication';
import ForgotPassword from './components/ForgotPassword';
import Dashboard from './components/Dashboard';
import DisposeMenu from './components/DisposeMenu';
import DisposeForm from './components/DisposeForm';
import TrackOrder from './components/TrackOrder';
import Profile from './components/Profile';
import DropdownMenu from './components/DropdownMenu';

function App() {
    const [user, setUser] = useState({
        name: 'John Doe',
        profilePicture: 'https://via.placeholder.com/150'
    });

    const [menuOpen, setMenuOpen] = useState(false);

    const handleLoginSuccess = (userData) => {
        setUser(userData);
    };

    const handleMenuItemClick = () => {
        setMenuOpen(false);
    };

    return (
        <Router>
            <HeaderComponent onMenuToggle={() => setMenuOpen(!menuOpen)} />
            <DropdownMenu isOpen={menuOpen} onMenuItemClick={handleMenuItemClick} />
            <div className="container">
                <Routes>
                    <Route path="/" element={<Authentication onLoginSuccess={handleLoginSuccess} />} />
                    <Route path="/forgot-password" element={<ForgotPassword />} />
                    <Route path="/dashboard" element={<Dashboard user={user} />} />
                    <Route path="/dispose" element={<DisposeMenu />} />
                    <Route path="/dispose/:itemType" element={<DisposeForm />} />
                    <Route path="/track-order" element={<TrackOrder />} />
                    <Route path="/profile" element={<Profile profilePicture={user.profilePicture} name={user.name} />} />
                    <Route path="/rewards" element={<div>Rewards Page</div>} />
                    <Route path="/customer-support" element={<div>Customer Support Page</div>} />
                    <Route path="/about-us" element={<div>About Us/Support Us Page</div>} />
                </Routes>
            </div>

        </Router>
    );
}

export default App;
