import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './DisposeForm.css';

const DisposeForm = () => {
    const { itemType } = useParams();
    const navigate = useNavigate();
    const [brand, setBrand] = useState('');
    const [yearsUsed, setYearsUsed] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('brand', brand);
        formData.append('yearsUsed', yearsUsed);
        formData.append('description', description);
        formData.append('image', image);

        try {
            const response = await fetch(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            console.log('Success:', data);
            navigate('/dispose');
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
            <h1>Dispose {itemType}</h1>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h2 className="text-center">Dispose {itemType}</h2>
                        <div className="card-body">
                            <form onSubmit={handleSubmit}>
                                <div className="form-group mb-2">
                                    <label className="form-label">Brand:</label>
                                    <input
                                        type="text"
                                        placeholder="Enter brand"
                                        name="brand"
                                        className="form-control"
                                        value={brand}
                                        onChange={(e) => setBrand(e.target.value)}
                                    />
                                </div>

                                <div className="form-group mb-2">
                                    <label className="form-label">Number of Years Used:</label>
                                    <input
                                        type="number"
                                        placeholder="Enter number of years used"
                                        name="yearsUsed"
                                        className="form-control"
                                        value={yearsUsed}
                                        onChange={(e) => setYearsUsed(e.target.value)}
                                    />
                                </div>

                                <div className="form-group mb-2">
                                    <label className="form-label">Description:</label>
                                    <textarea
                                        placeholder="Enter description"
                                        name="description"
                                        className="form-control"
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                    />
                                </div>

                                <div className="form-group mb-2">
                                    <label className="form-label">Upload Image:</label>
                                    <input
                                        type="file"
                                        name="image"
                                        className="form-control"
                                        onChange={(e) => setImage(e.target.files[0])}
                                    />
                                </div>

                                <button type="submit" className="btn btn-success">Submit</button>
                                <button type="button" className="btn btn-danger" onClick={() => navigate('/dispose')} style={{ marginLeft: "10px" }}>Cancel</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DisposeForm;
