// ItemForm.js

import React, { useState } from 'react';
import axios from 'axios';

const ItemForm = ({ itemType }) => {
    const [formData] = useState({
        // Define state variables to hold form data
        // Example: name, description, imageFile, etc.
    });

    // const handleChange = (e) => {
    //     // Update formData state based on user input
    //     setFormData({ ...formData, [e.target.name]: e.target.value });
    // };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Send POST request to backend API
            const response = await axios.post(`http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`, formData);
            console.log('Item created:', response.data);
            // Optionally, perform additional actions after successful form submission
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Form fields for item details */}
            {/* Example: input fields, textareas, file upload, etc. */}
            <button type="submit">Submit</button>
        </form>
    );
};

export default ItemForm;
