// ItemForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const ItemForm = () => {
    const { itemType } = useParams();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        brand: '',
        yearsUsed: '', // Updated to match backend naming convention
        description: '',
        imageUrl: null
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleImageChange = (e) => {
        setFormData({ ...formData, imageUrl: e.target.files[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('brand', formData.brand);
        formDataToSend.append('yearsUsed', formData.yearsUsed); // Updated to match backend naming convention
        formDataToSend.append('description', formData.description);
        formDataToSend.append('imageUrl', formData.imageUrl);

        try {
            const response = await axios.post(
                `http://localhost:8090/api/v1/items/${itemType.toLowerCase()}`,
                formDataToSend,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                }
            );
            console.log('Item created:', response.data);
            navigate('/dispose'); // Redirect to DisposeMenu after successful submission
        } catch (error) {
            console.error('Error creating item:', error);
        }
    };

    return (
        <div className="form-container">
            <h2>Add {itemType}</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="brand">Brand:</label>
                    <input
                        type="text"
                        id="brand"
                        name="brand"
                        value={formData.brand}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="yearsUsed">Years Used:</label> {/* Updated to match backend naming convention */}
                    <input
                        type="number"
                        id="yearsUsed"
                        name="yearsUsed"
                        value={formData.yearsUsed}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="imageUrl">Upload Image:</label> {/* Updated to match backend naming convention */}
                    <input
                        type="file"
                        id="imageUrl"
                        name="imageUrl"
                        onChange={handleImageChange}
                        required
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default ItemForm;
