import React, { useState, useEffect } from 'react';

const TrackOrder = ({ progress }) => {
    const steps = [33, 66, 100]; // Define the progress at which each step is accomplished

    return (
        <div className="p-8 bg-gray-900 text-white rounded-lg shadow-lg max-w-xl mx-auto">
            <h1 className="text-2xl font-bold mb-6">Track Your Order</h1>
            <div className="w-full bg-gray-700 rounded-full flex justify-between mb-6">
                {steps.map((step, index) => (
                    <div
                        key={index}
                        className={`w-10 h-10 rounded-full flex items-center justify-center ${
                            progress >= step ? 'bg-green-500' : 'bg-yellow-300'
                        }`}
                    >
                        <span className="text-white font-bold">{index + 1}</span>
                    </div>
                ))}
            </div>
            <div className="w-full bg-gray-700 rounded-full h-8">
                <div
                    className="bg-green-500 h-8 rounded-full transition-all duration-300"
                    style={{ width: `${progress}%` }}
                />
            </div>
        </div>
    );
};

const App = () => {
    const [progress, setProgress] = useState(0); // Initialize progress state to 0

    // Function to simulate order progress
    const simulateProgress = () => {
        setProgress((prevProgress) => {
            if (prevProgress >= 100) {
                return 0; // Reset progress to 0 if it's 100 or more
            } else {
                return prevProgress + 10; // Otherwise, increase progress by 10
            }
        });
    };

    // Automatically simulate progress at intervals
    useEffect(() => {
        const interval = setInterval(simulateProgress, 1000);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900">
            <TrackOrder progress={progress} />
        </div>
    );
};

export default App;
